import React, { createRef } from "react";
import "./App.css";
import axios from "axios";
import Add from "./component/Add";

class App extends React.Component {
  state = { people: [], first: "", last: "" };
  btnRef = createRef();

  componentDidMount = async () => {
    const { data } = await axios.get(
      "https://61c386e69cfb8f0017a3ebf0.mockapi.io/people"
    );
    console.log(data);
    console.log("cl");

    this.setState({ people: data });
  };

  add = async (first, last) => {
    const newItem = {
      first: first,
      last: last,
    };
    const { data } = await axios.post(
      "https://61c386e69cfb8f0017a3ebf0.mockapi.io/people"
    );

    console.log(data);

    // this.setState((state) => {
    //   return { data: [...state.data, data] };
    // });
  };

  showInfo = () => {
    return (
      <div>
        {this.state.people.map((person) => {
          return (
            <div key={person.id}>
              {person.first}
              <span> </span>
              {person.last}
              <button onClick={(e) => this.delete(person.id, e)}>Delete</button>
              {/* <Update id={person} onUpdate={this.update} /> */}
            </div>
          );
        })}
      </div>
    );
  };

  render() {
    return (
      <div>
        <Add func={this.add} />
        {this.state.people && this.showInfo()}
      </div>
    );
  }
}

export default App;
