import React, { Component } from "react";

class Add extends Component {
  state = { first: "", last: "" };

  onChangeHandle = (e) => {
    // const newState = { ...this.state };
    const newState = ([e.target.name] = e.target.value);
    console.log(newState);
    // this.setState(newState);
  };
  render() {
    return (
      <div>
        <form onSubmit={this.onSubmitHandle}>
          <label>
            First Name
            <input
              type="text"
              name="first"
              onChange={this.onChangeHandle}
              required
              minLength={2}
            />
          </label>

          <label>
            Last Name
            <input
              type="text"
              name="first"
              onChange={this.onChangeHandle}
              required
              minLength={2}
            />
          </label>
          <button ref={this.btnRef} type="submit">
            Add
          </button>
        </form>
      </div>
    );
  }
}

export default Add;
