import React, { createRef } from "react";
import "./App.css";

class App extends React.Component {
  state = { people: null, first: "", last: "" };
  btnRef = createRef();

  async componentDidMount() {
    const { data } = await axios.get();

    this.state({ people: data });
  }

  showInfo = () => {
    return (
      <div>
        {this.state.data.map((person) => {
          return (
            <div key={person.id}>
              {person.first}
              {person.last}
              <button onClick={(e) => this.delete(person.id, e)}>Delete</button>
              <Update id={person} onUpdate={this.update} />
            </div>
          );
        })}
      </div>
    );
  };

  render() {
    return (
      <div>
        <form onSubmit={this.onSubmitHandle}>
          <label>
            First Name
            <input
              type="text"
              name="first"
              onChange={this.onChangeHandle}
              required
              minLength={2}
            />
          </label>
          <label>
            Last Name
            <input
              type="text"
              name="first"
              onChange={this.onChangeHandle}
              required
              minLength={2}
            />
          </label>
          <button ref={this.btnRef} type="submit">
            Add
          </button>
        </form>
        {this.state.data && this.showInfo()}
      </div>
    );
  }
}

export default App;
